mapper.select:
SELECT

FROM SYS_PERMISSION t
WHERE id = #{id}

select * from(select t.*,rownum rn from(
	SELECT
	
	FROM SYS_PERMISSION t
	
	
) t where rownum < = #{page}*#{rows}) where rn > (#{page}-1)*#{rows}

select sm.shiro || ':' || sp.id id, su.name || '[' || su.user_name || ']' || '-' || sm.name || '-' || sp.name name from sys_user su left join sys_user_role sur on su.id = sur.user_id left join sys_role sr on sur.role_id = sr.id left join sys_role_module srm on sr.id = srm.role_id left join sys_module sm on srm.module_id = sm.id left join sys_module_permission smp on sm.id = smp.module_id left join sys_permission sp on smp.permission_id = sp.id
1=1
and su.user_name=#{userName}

mapper.insert:
	INSERT INTO SYS_PERMISSION (
    	
    	sequence,
    	name,
	    id
	)
	VALUES (
		
		#{sequence},
		#{name},
	    #{id}
	)

mapper.update:
	UPDATE SYS_PERMISSION
	
    	
    	
		sequence=#{sequence},
		
    	
		name=#{name},
		
	    id = #{id}
	
	WHERE id = #{id}
